from tkinter import *
import base64


icon_base64=''

with open('temp_icon.ico', 'wb') as temp_icon_file:
    temp_icon_file.write(base64.b64decode(icon_base64))  



root = Tk()
root.title('CALCULATOR')
root.iconbitmap('temp_icon.ico')

# Entry Widget
e = Entry(root, width=20, borderwidth=5, font=('Helvetica', 20), justify="right")
e.grid(row=0, column=0, columnspan=4, padx=10, pady=10, ipady=10)

# Function to handle button clicks
def click_action(number):
    current = e.get()
    e.delete(0, END)
    e.insert(0, current + str(number))

# Function to clear the entry
def clear_button():
    e.delete(0, END)

# Function to perform operations
def perform_operation(op):
    global first_number, operator
    operator = op
    first_number = float(e.get())
    e.delete(0, END)

# Function to calculate and display the result
def calculate_result():
    second_number = float(e.get())
    e.delete(0, END)
    if operator == "+":
        result = first_number + second_number
    elif operator == "-":
        result = first_number - second_number
    elif operator == "*":
        result = first_number * second_number
    elif operator == "/":
        if second_number == 0:
            e.insert(0, "Error")
            return
        result = first_number / second_number
    e.insert(0, result)

# Define buttons with custom styles
button_style = {'padx': 20, 'pady': 20, 'font': ('Helvetica', 18), 'bg': '#333', 'fg': 'white'}
button_1 = Button(root, text='1', command=lambda: click_action(1), **button_style)
button_2 = Button(root, text='2', command=lambda: click_action(2), **button_style)
button_3 = Button(root, text='3', command=lambda: click_action(3), **button_style)
button_4 = Button(root, text='4', command=lambda: click_action(4), **button_style)
button_5 = Button(root, text='5', command=lambda: click_action(5), **button_style)
button_6 = Button(root, text='6', command=lambda: click_action(6), **button_style)
button_7 = Button(root, text='7', command=lambda: click_action(7), **button_style)
button_8 = Button(root, text='8', command=lambda: click_action(8), **button_style)
button_9 = Button(root, text='9', command=lambda: click_action(9), **button_style)
button_0 = Button(root, text='0', command=lambda: click_action(0), **button_style)
button_clear = Button(root, text='C', command=clear_button, **button_style)
button_add = Button(root, text='+', command=lambda: perform_operation("+"), **button_style)
button_subtract = Button(root, text='-', command=lambda: perform_operation("-"), **button_style)
button_multiply = Button(root, text='*', command=lambda: perform_operation("*"), **button_style)
button_divide = Button(root, text='/', command=lambda: perform_operation("/"), **button_style)
button_equals = Button(root, text='=', command=calculate_result, **button_style)

# Arrange buttons on the grid
buttons = [
    [button_7, button_8, button_9, button_divide],
    [button_4, button_5, button_6, button_multiply],
    [button_1, button_2, button_3, button_subtract],
    [button_0, button_clear, button_equals, button_add]
]

for i, row in enumerate(buttons):
    for j, button in enumerate(row):
        button.grid(row=i+1, column=j, sticky='nsew')

# Make rows and columns expandable
for i in range(4):
    root.grid_rowconfigure(i, weight=1)
    root.grid_columnconfigure(i, weight=1)

root.mainloop()
